INSERT INTO `record_exercise_type` (`sid`, `exercise_name`, `exercise_description`, `exercise_img`, `exercise_vid`, `frontBackLow`, `status`) VALUES
(1, '槓鈴深蹲', '雙腳與肩同寬，腳尖朝前。彎曲膝蓋，降低身體直到大腿與地面平行，然後站起來。', '槓鈴深蹲.jpg', 'https://www.youtube.com/embed/gcNh17Ckjgg?start=154', 2, 1),
(2, '槓鈴臥推', '躺在平板凳上，抓住槓鈴使手臂與肩同寬，然後將槓鈴降低到胸前，再推回原位。', '槓鈴臥推.jpg', 'https://www.youtube.com/embed/4Y2ZdHCOXok?start=18', 0, 1),
(3, '槓鈴硬舉', '雙腳與肩同寬，握住槓鈴，然後通過膝蓋和臀部的伸展將重量從地面上抬起。', '槓鈴硬舉.jpg', 'https://www.youtube.com/embed/VL5Ab0T07e4?start=195', 2, 1),
(4, '弓箭步', '一腳向前邁步，降低身體直到兩腿都彎曲成90度，然後向上推起，換另一隻腳重複練習。', '弓箭步.jpg', 'https://www.youtube.com/embed/3XDriUn0udo?start=39', 2, 1),
(5, '引體向上', '以肩同寬握住橫向的懸浮杠，然後拉起身體直到下巴高於橫杆，再降下身體。', '引體向上.jpg', 'https://www.youtube.com/embed/sIvJTfGxdFo?start=219', 1, 1),
(6, '伏地挺身', '從平板支撐的姿勢開始，以肩同寬的手距降低身體，直到胸部碰到地面，然後推回原位。', '伏地挺身.jpg', 'https://www.youtube.com/embed/AhdtowFDKT0?start=104', 0, 1),
(7, '啞鈴二頭彎舉', '雙腳與肩同寬，握住一支啞鈴，將其向上彎曲到肩膀，然後降低到原位。', '啞鈴二頭彎舉.jpg', 'https://www.youtube.com/embed/ykJmrZ5v0Oo?start=4', 0, 1),
(8, '三頭肌板凳撐體', '坐在長凳邊緣，雙手握住後方邊緣，然後彎曲肘部降低身體，再推回原位。', '三頭肌板凳撐體.jpg', 'https://www.youtube.com/embed/jdFzYGmvDyg?start=120', 1, 1),
(9, '機器腿推舉', '坐在機器上，腳踏在平台上，然後用腿推開平台，再降低到原位。', '機器腿推舉.jpg', 'https://www.youtube.com/embed/IZxyjW7MPJQ?start=4', 2, 1),
(10, '三頭纜繩下拉', '坐在設備下面，握住繩子，然後向下拉到手伸直，然後緩慢地返回原位。', '三頭纜繩下拉.jpg', 'https://www.youtube.com/embed/tMyFe-IL7Ks?start=412', 1, 1),
(11, '卷腹', '這是一種腹肌鍛煉，主要鍛煉腹肌。 你平躺在地上，抬起雙腿，收緊腹部，使上半身離地，然後再慢慢放下。 ', '卷腹.jpg', 'https://www.youtube.com/embed/2_e4I-brfqs?start=60', 0, 1),
(12, '啞鈴側平舉', '這是一種鍛煉，主要鍛煉胸部肌肉的鍛煉。 你躺在板凳上，用啞鈴做“V”字型，然後慢慢下放，再向上舉起。 ', '啞鈴側平舉.jpg', 'https://www.youtube.com/embed/v_ZkxWzYnMc?start=56', 1, 1),
(13, '側平板支撐', '這是一種腰腹肌鍛煉，主要鍛煉腹橫肌和腰肌。 你側著身子，用手肘和膝蓋支撐地面，然後使身體離地，維持一定的時間。 ', '側平板支撐.jpg', 'https://www.youtube.com/embed/2_e4I-brfqs?start=210', 1, 1),
(14, '提踵', '這是一種腿部鍛煉，主要鍛煉小腿肌肉。 你站在踏板上，先讓腳跟下降到低點，然後用小腿力量將身體提起來，再慢慢放下。 ', '提踵.jpg', 'https://www.youtube.com/embed/7gguihMNVSE?start=381', 2, 1),
(15, '平板支撐', '這是一種腹肌鍛煉，主要鍛煉腹部肌肉。 你採取仰臥的姿勢，支撐膝蓋和前臂，使身體保持平衡狀態，直到時間結束。 ', '平板支撐.jpg', 'https://www.youtube.com/embed/pSHjTRCQxIw?start=2', 0, 1),
(16, '啞鈴推舉', '這是一種肩部鍛煉，主要鍛煉肩前束和三角肌。 你雙腳併攏站立，將負重啞鈴放在肩膀兩側，用雙手向上推舉，直到臂展完全伸直。 ', '啞鈴推舉.jpg', 'https://www.youtube.com/embed/0JfYxMRsUCQ?start=5', 0, 1),
(17, '啞鈴俯身划船', '這是一種鍛煉，主要鍛煉背部和手臂的鍛煉。 你俯身，抓住啞鈴，手臂伸直，然後將啞鈴拉向胸部，再慢慢放下。 ', '啞鈴俯身划船.jpg', 'https://www.youtube.com/embed/pYcpY20QaE8?start=11', 1, 1),
(18, '槓鈴肩推', '這是一種肩部鍛煉，主要鍛煉肩前束和三角肌。 你雙腳併攏站立，將槓鈴放在肩膀前方，用雙手向上推舉，直到臂展完全伸直。 ', '槓鈴肩推.jpg', 'https://www.youtube.com/embed/_RlRDWO2jfg?start=13', 0, 1),
(19, '窄距伏地挺身', '這是一種鍛煉，主要鍛煉三頭肌的鍛煉。你雙手放在地上，兩手指尖相碰，然後彎曲肘部，使身體向地面靠近，再用三頭肌力量將身體推回到起始位置。', '窄距伏地挺身.jpg', 'https://www.youtube.com/embed/IcWPQo1i0k4?start=3', 0, 1),
(20, '雙槓撐體', '這是一種鍛煉，主要鍛煉三頭肌的鍛煉。用雙手握住杠桿，將身體向上推，然後彎曲肘部，直到上臂與地面成平行，然後再慢慢放回到起始位置。', '雙槓撐體.jpg', 'https://www.youtube.com/embed/sM6XUdt1rm4?start=12', 0, 1),
(21, '槓鈴直腿硬舉', '如同傳統硬舉一樣，動作起始直接從向心收縮開始，但它臀位更高，且上半身更平行於地面。', '槓鈴直腿硬舉.jpg', 'https://www.youtube.com/embed/1uDiW5--rAE?start=14', 2, 1),
(22, '羅馬椅', '手拿槓片或是徒手的方式，將身體靠在羅馬椅上，確保椅墊約在髖關節的地方。用腿後側與臀部的力量，並注意核心與束脊肌穩定收緊，將上半身往上抬起，讓身體與羅馬椅呈現平行的一直線。', '羅馬椅.jpg', 'https://www.youtube.com/embed/H8Swl1N-uis?start=20', 1, 1),
(23, '啞鈴前平舉', '握著啞鈴，雙手自然下垂，肩膀放鬆。慢慢將啞鈴提升至肩平，保持姿勢穩定，再慢慢降回初始位置。重複動作。', '啞鈴前平舉.jpg', 'https://www.youtube.com/embed/-t7fuZ0KhDA?start=3', 0, 1),
(24, '纜繩側平舉', '握住纜繩，身體站立，腳稍微分開。一手握緊纜繩，手臂自然下垂。以肩膀為軸心，慢慢將手臂往側邊平舉，直到與地面平行。然後緩慢地回到初始位置，完成一次側平舉動作。重複動作，再換另一邊。', '纜繩側平舉.jpg', 'https://www.youtube.com/embed/VOAvDZGHOOI?start=62', 0, 1),
(25, '啞鈴集中彎舉', '坐在椅子上，腳平放地面，背部挺直。握住啞鈴，雙手自然垂放身體兩側。雙手同時彎曲向上，將啞鈴集中向肩膀靠近，然後緩慢地放回初始位置。保持手臂姿勢穩定，重複動作完成集中彎舉。', '啞鈴集中彎舉.jpg', NULL, 0, 1),
(26, '坐姿啞鈴彎舉', '坐在椅子上，握住啞鈴，雙手自然垂放身體兩側。一手或兩手同時彎曲向上，將啞鈴從大腿前方往肩膀靠近，直到手臂彎曲到最大程度。然後慢慢放回初始位置，重複動作完成啞鈴彎舉。', '坐姿啞鈴彎舉.jpg', NULL, 0, 1),
(27, 'V型坐姿保持', 'V型坐姿保持，背部離地，保持姿勢穩定。重複動作。', 'V型坐姿保持.jpg', NULL, 0, 1),
(28, '纜繩收腹', '纜繩收腹，站在纜繩機前，握住纜繩。屈膝下蹲，手臂伸直放在頭頂上方，然後用腹肌力量拉下纜繩至腹部緊縮，再緩慢放回初始位置', '纜繩收腹.jpg', NULL, 0, 1),
(29, '坐姿提踵', '坐在椅子上，雙腳平放地面，腳尖踮起抬高腳跟，再緩慢放回原位。重複動作。', '坐姿提踵.jpg', NULL, 2, 1),
(30, '槓鈴前臂彎舉', '手臂伸直，手握著槓鈴，手心朝上。手腕維持固定，只使用手部力量將槓鈴往上彎曲', '槓鈴前臂彎舉.jpg', NULL, 0, 1),
(31, '農夫走路', '手持啞鈴或壺鈴，核心收緊，往前走。此動作訓練核心在不穩定的情況下的能力', '農夫走路.jpg', NULL, 0, 1),
(32, '纜繩後踢', '纜繩後踢，站立面對纜繩機，用腿後方向上方踢出，強化臀部。', '纜繩後踢.jpg', NULL, 2, 1),
(33, '槓鈴臀推', '槓鈴臀推，仰躺於地，肩部靠椅子，槓鈴放在髖部，臀部向上推舉，加強臀部肌肉。', '槓鈴臀推.jpg', NULL, 2, 1);
